import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import MetadataDisplay from './MetadataDisplay';
import Preview from './Preview';

function DetailsPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { metadata, fileName, file } = location.state || {};

  if (!metadata) {
    return <p>No metadata to display. Please go back and upload a file.</p>;
  }

  return (
    <div className="container min-vh-100 bg-light py-5 d-flex flex-column justify-content-center">
      <div className="position-relative py-3 mx-auto" style={{maxWidth: '600px'}}>
        <div className="position-absolute top-0 start-0 w-100 h-100 bg-info bg-gradient shadow rounded-3" style={{zIndex: 0, transform: 'skewY(-6deg)'}}></div>
        <div className="position-relative p-4 bg-white shadow rounded-3" style={{zIndex: 1}}>
          <div className="mx-auto" style={{maxWidth: '400px'}}>
            <h1 className="h4 fw-semibold text-dark text-center">Metadata Details for {fileName}</h1>
            <div className="mt-4 d-flex flex-column flex-md-row">
              <div className="col-md-6 pe-md-4">
                <MetadataDisplay metadata={metadata} />
                <button
                  onClick={() => navigate('/')} 
                  className="mt-4 w-100 btn btn-primary"
                >
                  Reveal another Shot
                </button>
              </div>
              <div className="col-md-6 ps-md-4 mt-4 mt-md-0">
                <Preview file={file} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DetailsPage;
