import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import MetadataDisplay from './MetadataDisplay';
import Preview from './Preview';

function DetailsPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { metadata, fileName, file } = location.state || {};

  if (!metadata) {
    return <p>No metadata to display. Please go back and upload a file.</p>;
  }

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-lightBlue-500 shadow-lg transform -skew-y-6 sm:skew-y-0 sm:rotate-3 sm:rounded-3xl"></div>
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-md mx-auto">
            <h1 className="text-2xl font-semibold text-gray-900 text-center">Metadata Details for {fileName}</h1>
            <div className="mt-4 flex flex-col md:flex-row">
              <div className="md:w-1/2 md:pr-4">
                <MetadataDisplay metadata={metadata} />
                <button
                  onClick={() => navigate('/')}
                  className="mt-4 w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                >
                  Reveal another Shot
                </button>
              </div>
              <div className="md:w-1/2 md:pl-4 mt-4 md:mt-0">
                <Preview file={file} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DetailsPage;
