import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import MetadataDisplay from './components/MetadataDisplay';
import Message from './components/Message';
import LoadingSpinner from './components/LoadingSpinner';
import Preview from './components/Preview';
import { extractMetadata } from './utils/api';

function App() {
  const [metadata, setMetadata] = useState(null);
  const [fileName, setFileName] = useState('');
  const [message, setMessage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [file, setFile] = useState(null);

  const handleFileUpload = async (uploadedFile) => {
    setLoading(true);
    setMessage(null);
    setMetadata(null);
    setFile(uploadedFile);

    try {
      const data = await extractMetadata(uploadedFile);
      setMetadata(data.metadata);
      setFileName(data.fileName);
      setMessage({ type: 'success', text: 'Metadata extracted successfully!' });
    } catch (error) {
      console.error('Error:', error);
      setMessage({ type: 'error', text: error.message || 'An unexpected error occurred.' });
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setMetadata(null);
    setFileName('');
    setMessage(null);
    setLoading(false);
    setFile(null);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-lightBlue-500 shadow-lg transform -skew-y-6 sm:skew-y-0 sm:rotate-3 sm:rounded-3xl"></div>
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-md mx-auto">
            <div>
              <img src="/logo.png" alt="FirstShot Logo" className="mx-auto h-24 w-auto" />
              <h1 className="text-2xl font-semibold text-gray-900 text-center mt-4">FirstShot</h1>
              <p className="mt-2 text-sm text-gray-600">Every shot has a story, we Reveal it!</p>
            </div>
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <FileUpload onFileUpload={handleFileUpload} />
                {loading && <LoadingSpinner />}
                {message && <Message type={message.type} text={message.text} />}
                {metadata && (
                  <div className="mt-4 flex flex-col md:flex-row">
                    <div className="md:w-1/2 md:pr-4">
                      <h2 className="text-xl font-semibold text-gray-900">Extracted Metadata for {fileName}</h2>
                      <MetadataDisplay metadata={metadata} />
                      <button
                        onClick={handleClear}
                        className="mt-4 w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                      >
                        Clear
                      </button>
                    </div>
                    <div className="md:w-1/2 md:pl-4 mt-4 md:mt-0">
                      <Preview file={file} />
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
