import React, { useEffect, useState } from 'react';

function Preview({ file }) {
  const [previewUrl, setPreviewUrl] = useState(null);
  const [fileType, setFileType] = useState(null);

  useEffect(() => {
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
        setFileType(file.type.split('/')[0]); // 'image', 'video', 'audio', 'application', etc.
      };
      reader.readAsDataURL(file);
    } else {
      setPreviewUrl(null);
      setFileType(null);
    }
  }, [file]);

  if (!previewUrl) {
    return null; // Or a placeholder if you prefer
  }

  const renderPreview = () => {
    switch (fileType) {
      case 'image':
        return <img src={previewUrl} alt="Preview" className="max-w-full h-auto rounded-md shadow-md" />;
      case 'video':
        return (
          <video controls className="max-w-full h-auto rounded-md shadow-md">
            <source src={previewUrl} type={file.type} />
            Your browser does not support the video tag.
          </video>
        );
      case 'audio':
        return (
          <audio controls className="max-w-full h-auto rounded-md shadow-md">
            <source src={previewUrl} type={file.type} />
            Your browser does not support the audio tag.
          </audio>
        );
      case 'application':
        // For PDFs, you might embed them directly if supported by the browser
        if (file.type === 'application/pdf') {
          return (
            <iframe
              src={previewUrl}
              title="PDF Preview"
              className="w-full h-96 rounded-md shadow-md"
              style={{ border: 'none' }}
            ></iframe>
          );
        }
        return <p className="text-gray-600">No direct preview available for {file.type} files.</p>;
      default:
        return <p className="text-gray-600">No direct preview available for this file type.</p>;
    }
  };

  return (
    <div className="mt-4 p-4 bg-gray-50 rounded-md shadow-inner">
      <h3 className="text-lg font-medium text-gray-900 mb-2">File Preview:</h3>
      <div className="flex justify-center items-center">
        {renderPreview()}
      </div>
    </div>
  );
}

export default Preview;
